import { useContext, useState } from 'react'
import Context from '../store/context';
import { Menu, Icon, Button } from 'antd';

// CSS Import 
import '../styles/SlideOptions.css'

const { SubMenu } = Menu;
const env_names = []
for (let i = 1; i < 16; i++) {
    env_names.push(`Ambiente ${i}`)
}

const SlideOptions = () => {
    const { state, actions } = useContext(Context)
    const options = ['Descripcion', 'Conexiones', 'Fuente', 'Campos']
    const handleClick = () => console.log("E")

    const createTable = param => () => {
        if (Object.keys(param).toString() === options[0]) {
            actions({ type: 'set-new-state', payload: { ...state, option_to_load: null } })
            setTimeout(() => actions({ type: 'set-new-state', payload: { ...state, option_to_load: Object.values(param) } }), 500)
        } else {
            alert("Ha ocurrido un error")
        }
    }

    return (
        <div className="slide-options-container">


            {
                env_names.map((x, i) =>
                    <Menu
                        onClick={handleClick}
                        style={{ width: 256 }}
                        defaultSelectedKeys={['1']}
                        //defaultOpenKeys={['sub1']}
                        mode="inline"
                        selectable="false"
                    >
                        <SubMenu
                            key={`sub${i + 1}`}
                            title={
                                <span>
                                    <Icon type="cloud-server" />
                                    <span>{x}</span>
                                </span>
                            }
                        >
                            {
                                options.map((y, j) =>
                                    <div style={{ width: '100%' }} className="d-flex justify-content-center">
                                        <div className="button-option-container">
                                            <Button onClick={createTable({ [y]: i })} key={j} block>{y}</Button>
                                        </div>
                                    </div>
                                )
                            }

                        </SubMenu>
                    </Menu>
                )
            }


        </div>
    )
}

export default SlideOptions
