import { useContext, useEffect } from 'react'
import Context from '../store/context'
import useGlobalState from '../store/useGlobalState';
import '../styles/Table.css'
import { Table } from 'antd'



const data = [
    {
        key: '1',
        name: 'John Brown',
        age: 32,
        address: 'New York No. 1 Lake Park',
    },
    {
        key: '2',
        name: 'Jim Green',
        age: 42,
        address: 'London No. 1 Lake Park',
    },
    {
        key: '3',
        name: 'Joe Black',
        age: 32,
        address: 'Sidney No. 1 Lake Park',
    },
    {
        key: '4',
        name: 'Disabled User',
        age: 99,
        address: 'Sidney No. 1 Lake Park',
    },
];


const SingleTable = () => {
    const { state, actions } = useContext(Context);
    const [showTable, setShowingTable] = useState(false)
    const columns = [];
    const data = []

    useEffect(async () => {
        Object.keys(state.data[state.option_to_load]).map(x => { columns.push({ title: x, dataIndex: x.toLowerCase() }) })

        await columns.shift()



        await Object.keys(state.data[state.option_to_load]).map(key => {
            Object.values(state.data[state.option_to_load]).map(value => {
                data[key] = value
            })
        })


        await console.log(data)

        await setShowingTable(true)
    }, [])


    return (
        <div className="table-container">
            {showTable ? <Table columns={columns} dataSource={data} /> : <div /> }
        </div>
    )
}


export default SingleTable
